import React, { useEffect, useState } from "react";
import { Box, Avatar, Typography, Button, Input } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";
import { acGetUsersRequest, acUpdateUserRequest } from "../../../Actions";

export default function MemberAvatar(props) {
  const users = useSelector((state) => state.users);
  const dispatch = useDispatch();
  const [inputValue, setInputValue] = useState({
    id: "",
    code: "",
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    country: "",
    date: "",
    image: "",
    password: "",
  });
  const [save, setSave] = useState(false);

  useEffect(() => {
    dispatch(acGetUsersRequest());
  }, []);

  useEffect(() => {
    var current = JSON.parse(localStorage.getItem("username"));
    if (users.length > 0) {
      var index = users.findIndex((user) => user.id === current);
      var user = users[index];
      setInputValue({
        code: user.code ? user.code : "",
        firstName: user.firstName ? user.firstName : "",
        lastName: user.lastName ? user.lastName : "",
        username: user.username ? user.username : "",
        email: user.email ? user.email : "",
        country: user.country ? user.country : "",
        password: user.password,
        id: user.id,
        image: user.image ? user.image : "",
        date: user.date,
      });
    }
  }, [users]);

  const handleFile = (e) => {
    var file = e.target.files[0];
    const fr = new FileReader();
    fr.readAsDataURL(file);
    fr.onloadend = () => {
      setInputValue({
        ...inputValue,
        image: fr.result,
      });
    };
  };

  const handleChange = (e) => {
    setInputValue({
      ...inputValue,
      [e.target.name]: e.target.value,
    });
  };

  const handleSave = () => {
    dispatch(acUpdateUserRequest(inputValue));
    setSave(true);
    setTimeout(() => {
      setSave(false);
    }, 5000);
  };
  return (
    <>
      <Box
        className="box mt-4"
        sx={{
          padding: "35px",
          borderRadius: "3px",
          backgroundColor: "#333",
        }}
      >
        <Avatar
          className="avatar-member"
          src={
            props.image
              ? props.image
              : "https://member.aorus.com/images/pic-default.jpg"
          }
        />
        <Typography variant="h5" className="text-center my-3" color="white">
          {props.username}
        </Typography>
        <Box sx={{ textAlign: "center" }}>
          <label className="w-100" htmlFor="contained-button-file">
            <Input
              accept="image/*"
              id="contained-button-file"
              multiple
              onChange={props.handleFile}
              sx={{ display: "none" }}
              type="file"
            />
            <Button
              variant="outlined"
              sx={{
                color: "#fff",
                background: "#000",
                border: "1px solid #fff",
                width: "100%",
              }}
              className="font-family-Ad"
              component="span"
            >
              Edit Profile Picture
            </Button>
          </label>
          <Button
            variant="outlined"
            sx={{
              color: "#fff",
              background: "#000",
              border: "1px solid #fff",
              marginTop: "10px",
              width: "100%",
            }}
            className="font-family-Ad"
            component="span"
          >
            Change Password
          </Button>
        </Box>
      </Box>
    </>
  );
}
