import React, { useEffect, useState } from "react";
import MemberAvatar from "../MemberAvatar";
import "./../styles.scss";
import { Box, Avatar, Typography, Button, Input } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";
import { acGetUsersRequest, acUpdateUserRequest } from "../../../Actions";

export default function Layout({ children }) {
  const users = useSelector((state) => state.users);
  const dispatch = useDispatch();
  const [inputValue, setInputValue] = useState({
    id: "",
    code: "",
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    country: "",
    date: "",
    image: "",
    password: "",
  });
  const [save, setSave] = useState(false);

  useEffect(() => {
    dispatch(acGetUsersRequest());
  }, []);

  useEffect(() => {
    var current = JSON.parse(localStorage.getItem("username"));
    if (users.length > 0) {
      var index = users.findIndex((user) => user.id === current);
      var user = users[index];
      setInputValue({
        code: user.code ? user.code : "",
        firstName: user.firstName ? user.firstName : "",
        lastName: user.lastName ? user.lastName : "",
        username: user.username ? user.username : "",
        email: user.email ? user.email : "",
        country: user.country ? user.country : "",
        password: user.password,
        id: user.id,
        image: user.image ? user.image : "",
        date: user.date,
      });
    }
  }, [users]);

  const handleFile = (e) => {
    var file = e.target.files[0];
    const fr = new FileReader();
    fr.readAsDataURL(file);
    fr.onloadend = () => {
      setInputValue({
        ...inputValue,
        image: fr.result,
      });
    };
  };

  const handleChange = (e) => {
    setInputValue({
      ...inputValue,
      [e.target.name]: e.target.value,
    });
  };

  const handleSave = () => {
    dispatch(acUpdateUserRequest(inputValue));
    setSave(true);
    setTimeout(() => {
      setSave(false);
    }, 5000);
  };
  return (
    <>
      <Box className="container-fluid body-account">
        <Box className="container h-100 body-account-content">
          <Grid container className="" spacing={3}>
            <Grid item xs={12} className="mt-5 mb-4 text-center">
              <Typography
                variant="h4"
                className="font-family-Ad"
                color="white "
              >
                My Account Details
              </Typography>
            </Grid>
            <Grid item xs={12} md={4}>
              <MemberAvatar
                handleFile={handleFile}
                username={inputValue.username}
                image={inputValue.image}
              />
            </Grid>
            <Grid item xs={12} md={8}>
              {children}
            </Grid>
          </Grid>
        </Box>
      </Box>
    </>
  );
}
